"use strict";window.addEventListener("DOMContentLoaded",()=>{scroll()});const scroll=()=>{const o=document.getElementById("description"),s=document.getElementById("image"),e=document.querySelectorAll(".block");window.addEventListener("scroll",function(){window.scrollY>250&&e.forEach(o=>{o.classList.add("show")}),window.scrollY>750&&(o.classList.add("show"),s.classList.add("show"))})};